import { ResponsiveChord } from "@nivo/chord"



export const ChordChart : React.FC < {data : Array<number>[]}> = (props) => {


    const data : Array<number>[] = props.data;
    return (

        <div className="mr-3 h-50 w-50">
      <div className="card bg-color">
        <div className="card-header">
          <h3 className="card-title ">Chord Chart</h3>
        </div>
        <div className="card-body">
          <div style={{height: 300, width: "auto"}}>
          <ResponsiveChord
        data={data}
        keys={[ 'January', 'Feburary', 'March', 'April', 'May' ]}
        margin={{ top: 60, right: 60, bottom: 90, left: 60 }}
        valueFormat=".2f"
        padAngle={0.02}
        innerRadiusRatio={0.96}
        innerRadiusOffset={0.02}
        inactiveArcOpacity={0.25}
        arcBorderColor={{
            from: 'color',
            modifiers: [
                [
                    'darker',
                    0.6
                ]
            ]
        }}
        activeRibbonOpacity={0.75}
        inactiveRibbonOpacity={0.25}
        ribbonBorderColor={{
            from: 'color',
            modifiers: [
                [
                    'darker',
                    0.6
                ]
            ]
        }}
        labelRotation={-90}
        labelTextColor={{
            from: 'color',
            modifiers: [
                [
                    'darker',
                    1
                ]
            ]
        }}
        colors={{ scheme: 'nivo' }}
        motionConfig="stiff"
        legends={[
            {
                anchor: 'bottom',
                direction: 'row',
                justify: false,
                translateX: 0,
                translateY: 70,
                itemWidth: 80,
                itemHeight: 14,
                itemsSpacing: 0,
                itemTextColor: '#999',
                itemDirection: 'left-to-right',
                symbolSize: 12,
                symbolShape: 'circle',
                effects: [
                    {
                        on: 'hover',
                        style: {
                            itemTextColor: '#000'
                        }
                    }
                ]
            }
        ]}
    />

          </div>
        </div>
      </div>
    </div>










    )
}
